    //****************************************************************************/
    QtResourceMain::QtResourceMain(const QString& iconDir, QWidget* parent) : QMainWindow(parent), mIsClosing(false)
    {
        mIconDir = iconDir;

        // Perform standard functions
        createActions();
        createMenus();
        createToolBars();
        createStatusBar();
        createDockWindows();

        // Set the title
        setWindowTitle(QString("ContentBrowser"));
        showMaximized();
    }

