//****************************************************************************/
RendererDockWidget::RendererDockWidget(QString title, MainWindow* parent, Qt::WindowFlags flags) : 
	QDockWidget (title, parent, flags), 
	mParent(parent)
{
    mInnerMain = new QMainWindow();
    setWidget(mInnerMain);

    // Perform standard functions
    createActions();
    createMenus();
    createToolBars();

    mOgreWidget = new QOgre::Widget(mInnerMain);
    mInnerMain->setCentralWidget(mOgreWidget);
    parent->getOgreManager()->registerQtOgreWidget(1, mOgreWidget);
    mOgreWidget->go(parent->getOgreManager()->getOgreRoot()); 


}

